import React, { useState, useRef } from "react";
import ScheduleTable from "../../components/ScheduleTable/ScheduleTable";
import "./OrarToti.css";

const OrarToti = () => {
  const [activeCategory, setActiveCategory] = useState("Orar Studenți"); // Categoria activă
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const dropdownRefs = useRef([]);

  const toggleDropdown = (index, e) => {
    e.stopPropagation();
    setActiveDropdown((prev) => (prev === index ? null : index));
    setSelectedGroup(null);
  };

  const handleClickOutside = (event) => {
    if (dropdownRefs.current.every((ref) => ref && !ref.contains(event.target))) {
      setActiveDropdown(null);
    }
  };

  React.useEffect(() => {
    document.addEventListener("click", handleClickOutside);
    return () => {
      document.removeEventListener("click", handleClickOutside);
    };
  }, []);

  const studentButtons = [
    {
      label: "Anul 1",
      icon: "1️⃣",
      options: ["A1", "A2", "A3", "A4", "A5", "B1", "B2", "B3", "B4", "B5", "E1", "E2", "E3"],
    },
    {
      label: "Anul 2",
      icon: "2️⃣",
      options: ["A1", "A2", "A3", "A4", "A5", "B1", "B2", "B3", "B4", "B5", "E1", "E2", "E3"],
    },
    {
      label: "Anul 3",
      icon: "3️⃣",
      options: ["A1", "A2", "A3", "A4", "A5", "B1", "B2", "B3", "B4", "B5", "E1", "E2", "E3"],
    },
    {
      label: "Master Anul 1",
      icon: "🎓",
      options: [
        "Inteligența Artificială și Optimizare",
        "Ingineria Sistemelor Soft",
        "Lingvistica Computațională",
        "Studii Avansate în Informatică",
        "Sisteme Distribuite",
        "Securitatea Informațiilor",
      ],
    },
    {
      label: "Master Anul 2",
      icon: "🎓",
      options: [
        "Inteligența Artificială și Optimizare",
        "Ingineria Sistemelor Soft",
        "Lingvistica Computațională",
        "Studii Avansate în Informatică",
        "Sisteme Distribuite",
        "Securitatea Informațiilor",
      ],
    },
  ];

  return (
    <div className="orar-container">
      <div className="orar-titlu">
        <h1>Orar</h1>
        <h2>Alege o categorie pentru a vizualiza detalii</h2>
      </div>

      {/* Meniu de navigare pentru categorii */}
      <div className="orar-categories">
        {["Orar Studenți", "Orar Profesori", "Orar Săli", "Orar pe Discipline"].map((category) => (
          <button
            key={category}
            className={`category-button ${activeCategory === category ? "active" : ""}`}
            onClick={() => {
              setActiveCategory(category);
              setSelectedGroup(null); // Resetăm selecția
            }}
          >
            {category}
          </button>
        ))}
      </div>

      {/* Conținut pentru categoria selectată */}
      <div className="orar-content">
        {activeCategory === "Orar Studenți" && (
          <div className="orar-buttons">
            {studentButtons.map((button, index) => (
              <div
                className="dropdown-container"
                key={index}
                ref={(el) => (dropdownRefs.current[index] = el)}
              >
                <button
                  className="orar-button"
                  onClick={(e) => toggleDropdown(index, e)}
                >
                  <span className="icon">{button.icon}</span>
                  {button.label}
                </button>

                {activeDropdown === index && (
                  <div className="dropdown-menu">
                    {button.options.map((option, i) => (
                      <div key={i}>
                        <button
                          className="dropdown-item"
                          onClick={() => {
                            setSelectedGroup(`${button.label} - ${option}`); // Setăm grupa selectată
                            setActiveDropdown(null);
                          }}
                        >
                          {option}
                        </button>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            ))}
          </div>
        )}

        {activeCategory === "Orar Profesori" && (
          <div className="orar-placeholder">
            <h3>Orar Profesori</h3>
            <p>Funcționalitatea pentru orarul profesorilor va fi adăugată aici.</p>
          </div>
        )}

        {activeCategory === "Orar Săli" && (
          <div className="orar-placeholder">
            <h3>Orar Săli</h3>
            <p>Funcționalitatea pentru orarul sălilor va fi adăugată aici.</p>
          </div>
        )}

        {activeCategory === "Orar pe Discipline" && (
          <div className="orar-placeholder">
            <h3>Orar pe Discipline</h3>
            <p>Funcționalitatea pentru orarul pe discipline va fi adăugată aici.</p>
          </div>
        )}
      </div>

      {/* Afișăm tabelul gol pentru grupa selectată */}
      {selectedGroup && (
        <div className="orar-afisat">
          <h3>Orar pentru {selectedGroup}</h3>
          <ScheduleTable
            schedule={[]} // Tabel gol
            title={`Orar pentru ${selectedGroup}`}
          />
        </div>
      )}
    </div>
  );
};

export default OrarToti;
