import React, { useState, useRef } from "react";
import ScheduleTable from "../../components/ScheduleTable/ScheduleTable";
import "./OrarToti.css";

const OrarToti = () => {
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [section, setSection] = useState(null); // "studenti", "profesori", etc.
  const dropdownRefs = useRef([]);

  const toggleDropdown = (index, e) => {
    e.stopPropagation();
    setActiveDropdown((prev) => (prev === index ? null : index));
    setSelectedGroup(null);
  };

  const handleClickOutside = (event) => {
    if (dropdownRefs.current.every((ref) => ref && !ref.contains(event.target))) {
      setActiveDropdown(null);
    }
  };

  React.useEffect(() => {
    document.addEventListener("click", handleClickOutside);
    return () => {
      document.removeEventListener("click", handleClickOutside);
    };
  }, []);

  const buttons = [
    {
      label: "Anul 1",
      icon: "1Ô∏è‚É£",
      options: ["A1", "A2", "A3", "A4", "A5", "B1", "B2", "B3", "B4", "B5", "E1", "E2", "E3"],
    },
    {
      label: "Anul 2",
      icon: "2Ô∏è‚É£",
      options: ["A1", "A2", "A3", "A4", "A5", "B1", "B2", "B3", "B4", "B5", "E1", "E2", "E3"],
    },
    {
      label: "Anul 3",
      icon: "3Ô∏è‚É£",
      options: ["A1", "A2", "A3", "A4", "A5", "B1", "B2", "B3", "B4", "B5", "E1", "E2", "E3"],
    },
    {
      label: "Master Anul 1",
      icon: "üéì",
      options: [
        "Inteligen»õa ArtificialƒÉ »ôi Optimizare",
        "Ingineria Sistemelor Soft",
        "Lingvistica Computa»õionalƒÉ",
        "Studii Avansate √Æn InformaticƒÉ",
        "Sisteme Distribuite",
        "Securitatea Informa»õiilor",
      ],
    },
    {
      label: "Master Anul 2",
      icon: "üéì",
      options: [
        "Inteligen»õa ArtificialƒÉ »ôi Optimizare",
        "Ingineria Sistemelor Soft",
        "Lingvistica Computa»õionalƒÉ",
        "Studii Avansate √Æn InformaticƒÉ",
        "Sisteme Distribuite",
        "Securitatea Informa»õiilor",
      ],
    },
  ];

  return (
    <div className="orar-container">
      <div className="orar-titlu">
        <h1>Orar</h1>
        {!section && <h2>Alege o categorie</h2>}
        {section === "studenti" && <h2>Alege anul »ôi grupa</h2>}
      </div>

      {/* Meniu Principal */}
      {!section && (
        <div className="orar-buttons">
          <button className="orar-button" onClick={() => setSection("studenti")}>üéì Orar Studen»õi</button>
          <button className="orar-button" onClick={() => setSection("profesori")}>üë®‚Äçüè´ Orar Profesori</button>
          <button className="orar-button" onClick={() => setSection("sali")}>üè´ Orar SƒÉli</button>
          <button className="orar-button" onClick={() => setSection("discipline")}>üìö Orar Discipline</button>
        </div>
      )}

      {/* Submeniu Studen»õi */}
      {section === "studenti" && (
        <>
          <div className="orar-buttons">
            {buttons.map((button, index) => (
              <div
                className="dropdown-container"
                key={index}
                ref={(el) => (dropdownRefs.current[index] = el)}
              >
                <button className="orar-button" onClick={(e) => toggleDropdown(index, e)}>
                  <span className="icon">{button.icon}</span>
                  {button.label}
                </button>

                {activeDropdown === index && (
                  <div className="dropdown-menu">
                    {button.options.map((option, i) => (
                      <div key={i}>
                        <button
                          className="dropdown-item"
                          onClick={() => {
                            setSelectedGroup(`${button.label} - ${option}`);
                            setActiveDropdown(null);
                          }}
                        >
                          {option}
                        </button>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            ))}
          </div>

          {/* Afi»ôare tabel */}
          {selectedGroup && (
            <div className="orar-afisat">
              <h3>Orar pentru {selectedGroup}</h3>
              <ScheduleTable
                schedule={[]} // de completat cu date reale
                title={`Orar pentru ${selectedGroup}`}
              />
            </div>
          )}

          {/* Buton √énapoi */}
          <div className="inapoi-container">
            <button className="orar-button inapoi" onClick={() => {
              setSection(null);
              setSelectedGroup(null);
              setActiveDropdown(null);
            }}>
              üîô √énapoi
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default OrarToti;
