import React, { useState, useRef, useEffect } from "react";
import "./OrarToti.css";
import ScheduleTable from "./ScheduleTable"; // ImportƒÉm tabelul

const OrarToti = () => {
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [selectedOption, setSelectedOption] = useState(null);
  const [selectedYear, setSelectedYear] = useState(null); 
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [selectedProfesor, setSelectedProfesor] = useState(null); 
  const [orarGrupa, setOrarGrupa] = useState([]);
  const [orarProfesor, setOrarProfesor] = useState([]); 
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const dropdownRefs = useRef([]);

  const toggleDropdown = (index, e) => {
    e.stopPropagation();
    setActiveDropdown((prev) => (prev === index ? null : index));
    setSelectedOption(null);
    setSelectedYear(null); 
    setSelectedGroup(null);
    setOrarGrupa([]);
    setSelectedProfesor(null); 
    setOrarProfesor([]); 
  };

  // Func»õia pentru a prelua orarul grupei
  const fetchOrarGrupa = async (grupa) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`http://localhost:34101/orar/grupa/${encodeURIComponent(grupa)}`);
      if (!response.ok) {
        throw new Error('Eroare la fetch');
      }
      const data = await response.json();
      setOrarGrupa(data);
    } catch (err) {
      setError('Eroare la fetch');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // Func»õia pentru a prelua orarul profesorului
  const fetchOrarProfesor = async (profesor) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`http://localhost:34101/orar/profesor/${encodeURIComponent(profesor)}`);
      if (!response.ok) {
        throw new Error('Eroare la fetch');
      }
      const data = await response.json();
      setOrarProfesor(data);
    } catch (err) {
      setError('Eroare la fetch');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleClickOutside = (event) => {
    if (dropdownRefs.current.every((ref) => ref && !ref.contains(event.target))) {
      setActiveDropdown(null);
      setSelectedOption(null);
      setSelectedYear(null); 
      setSelectedGroup(null);
      setOrarGrupa([]);
      setSelectedProfesor(null);
      setOrarProfesor([]); 
    }
  };

  useEffect(() => {
    document.addEventListener("click", handleClickOutside);
    return () => {
      document.removeEventListener("click", handleClickOutside);
    };
  }, []);

  // Butoanele »ôi op»õiunile de selec»õie
  const buttons = [
    {
      label: "Orar studen»õi",
      icon: "üéì",
      options: ["Anul 1", "Anul 2", "Anul 3", "Master 1", "Master 2", "»òcoala doctoralƒÉ"],
    },
    {
      label: "Orar profesori",
      icon: "üë®‚Äçüè´",
      options: ["Vasilescu Andrei", "Profesor 2", "Profesor 3"],
    },
    {
      label: "Orar pe discipline de studiu",
      icon: "üìò",
      options: ["DisciplinƒÉ 1", "DisciplinƒÉ 2", "DisciplinƒÉ 3"],
    },
    {
      label: "Orar sƒÉli »ôi alte resurse",
      icon: "üè¢",
      options: ["Sala 1", "Sala 2", "Sala 3"],
    },
  ];

  return (
    <div className="orar-container">
      <div className="orar-titlu">
        <h1>Orar</h1>
        <h2>Alege o categorie pentru a vizualiza detalii</h2>
      </div>
      <div className="orar-buttons">
        {buttons.map((button, index) => (
          <div
            className="dropdown-container"
            key={index}
            ref={(el) => (dropdownRefs.current[index] = el)}
          >
            <button
              className="orar-button"
              onClick={(e) => toggleDropdown(index, e)}
            >
              <span className="icon">{button.icon}</span>
              {button.label}
            </button>

            {activeDropdown === index && (
              <div className="dropdown-menu">
                {button.options.map((option, i) => (
                  <div key={i}>
                    <button
                      className="dropdown-item"
                      onClick={() => {
                        setSelectedOption(option);
                        setSelectedYear(null); 
                        setSelectedGroup(null);
                        setOrarGrupa([]);
                        setSelectedProfesor(option); 
                        setOrarProfesor([]); 
                        fetchOrarProfesor(option); 
                      }}
                    >
                      {option}
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Afi»ôƒÉm orarul grupei doar dacƒÉ s-a selectat o grupƒÉ */}
      {selectedGroup && (
        <div className="orar-afisat">
          <h3>Orar pentru {selectedGroup}</h3>
          {loading && <p>Se √ÆncarcƒÉ...</p>}
          {error && <p>{error}</p>}
          <ScheduleTable schedule={orarGrupa} title={`Orar pentru grupa ${selectedGroup}`} />
        </div>
      )}

      {/* Afi»ôƒÉm orarul profesorului doar dacƒÉ s-a selectat un profesor */}
      {selectedProfesor && (
        <div className="orar-afisat">
          <h3>Orar pentru {selectedProfesor}</h3>
          {loading && <p>Se √ÆncarcƒÉ...</p>}
          {error && <p>{error}</p>}
          <ScheduleTable schedule={orarProfesor} title={`Orar pentru profesorul ${selectedProfesor}`} />
        </div>
      )}
    </div>
  );
};

export default OrarToti;
