import React, { useState } from "react";
import ScheduleTable from "../../components/ScheduleTable/ScheduleTable";
import "./OrarToti.css";

const OrarToti = () => {
  const [activeDropdown, setActiveDropdown] = useState(null); // Dropdown activ
  const [selectedGroup, setSelectedGroup] = useState(null); // Grupa selectată

  const studentYears = [
    {
      label: "Anul 1",
      groups: ["A1", "A2", "A3", "A4", "A5", "B1", "B2", "B3", "B4", "B5", "E1", "E2", "E3"],
    },
    {
      label: "Anul 2",
      groups: ["A1", "A2", "A3", "A4", "A5", "B1", "B2", "B3", "B4", "B5", "E1", "E2", "E3"],
    },
    {
      label: "Anul 3",
      groups: ["A1", "A2", "A3", "A4", "A5", "B1", "B2", "B3", "B4", "B5", "E1", "E2", "E3"],
    },
    {
      label: "Master Anul 1",
      groups: [
        "Inteligența Artificială și Optimizare",
        "Ingineria Sistemelor Soft",
        "Lingvistica Computațională",
        "Studii Avansate în Informatică",
        "Sisteme Distribuite",
        "Securitatea Informațiilor",
      ],
    },
    {
      label: "Master Anul 2",
      groups: [
        "Inteligența Artificială și Optimizare",
        "Ingineria Sistemelor Soft",
        "Lingvistica Computațională",
        "Studii Avansate în Informatică",
        "Sisteme Distribuite",
        "Securitatea Informațiilor",
      ],
    },
  ];

  const buttons = [
    { label: "Orar studenți", icon: "🎓" },
    { label: "Orar profesori", icon: "👨‍🏫" },
    { label: "Orar pe discipline de studiu", icon: "📘" },
    { label: "Orar săli și alte resurse", icon: "🏢" },
  ];

  return (
    <div className="orar-container">
      <div className="orar-titlu">
        <h1>Orar</h1>
        <h2>Alege o categorie pentru a vizualiza detalii</h2>
      </div>

      {/* Butoane principale */}
      <div className="orar-buttons">
        {buttons.map((button, index) => (
          <button
            key={index}
            className={`orar-button ${activeDropdown === index ? "active" : ""}`}
            onClick={() => {
              setActiveDropdown(activeDropdown === index ? null : index);
              setSelectedGroup(null); // Resetăm grupa selectată
            }}
          >
            <span className="icon">{button.icon}</span>
            {button.label}
          </button>
        ))}
      </div>

      {/* Dropdown pentru "Orar Studenți" */}
      {activeDropdown === 0 && (
        <div className="dropdown-menu">
          {studentYears.map((year, index) => (
            <div key={index} className="dropdown-item-container">
              <button
                className="dropdown-item"
                onClick={() =>
                  setSelectedGroup(selectedGroup === year.label ? null : year.label)
                }
              >
                {year.label}
              </button>
              {selectedGroup === year.label && (
                <div className="dropdown-submenu">
                  {year.groups.map((group, i) => (
                    <button
                      key={i}
                      className="dropdown-subitem"
                      onClick={() => setSelectedGroup(group)}
                    >
                      {group}
                    </button>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>
      )}

      {/* Afișăm tabelul pentru grupa selectată */}
      {selectedGroup && (
        <div className="orar-afisat">
          <h3>Orar pentru {selectedGroup}</h3>
          <ScheduleTable
            schedule={[]} // Tabel gol
            title={`Orar pentru ${selectedGroup}`}
          />
        </div>
      )}
    </div>
  );
};

export default OrarToti;
