import React, { useState, useRef, useEffect } from "react";
import { useNavigate, useLocation, useParams } from "react-router-dom"; // Importăm useLocation și useParams
import ScheduleTable from "../../components/ScheduleTable/ScheduleTable";
import "./OrarToti.css";

const OrarToti = () => {
  const navigate = useNavigate();
  const location = useLocation(); // Folosim useLocation pentru a obține URL-ul curent
  const { an, grupa } = useParams(); // Preluăm parametrii din URL (an și grupă)
  
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [section, setSection] = useState(null);
  const [scheduleData, setScheduleData] = useState([]);
  const dropdownRefs = useRef([]);

  // Dacă secțiunea "studenti" este aleasă din URL, setează secțiunea
  useEffect(() => {
    if (location.pathname.includes("studenti")) {
      setSection("studenti"); // Setează secțiunea de studenți
    }
  }, [location.pathname]);

  const toggleDropdown = (index, e) => {
    e.stopPropagation();
    setActiveDropdown((prev) => (prev === index ? null : index));
    setSelectedGroup(null);
  };

  const handleClickOutside = (event) => {
    if (dropdownRefs.current.every((ref) => ref && !ref.contains(event.target))) {
      setActiveDropdown(null);
    }
  };

  useEffect(() => {
    document.addEventListener("click", handleClickOutside);
    return () => {
      document.removeEventListener("click", handleClickOutside);
    };
  }, []);

  const buttons = [
    {
      label: "Anul 1",
      icon: "1️⃣",
      options: ["A1", "A2", "A3", "A4", "A5", "B1", "B2", "B3", "B4", "B5", "E1", "E2", "E3"],
    },
    {
      label: "Anul 2",
      icon: "2️⃣",
      options: ["A1", "A2", "A3", "A4", "A5", "B1", "B2", "B3", "B4", "B5", "E1", "E2", "E3"],
    },
    {
      label: "Anul 3",
      icon: "3️⃣",
      options: ["A1", "A2", "A3", "A4", "A5", "B1", "B2", "B3", "B4", "B5", "E1", "E2", "E3"],
    },
    {
      label: "Master Anul 1",
      icon: "🎓",
      options: [
        "Inteligența Artificială și Optimizare",
        "Ingineria Sistemelor Soft",
        "Lingvistica Computațională",
        "Studii Avansate în Informatică",
        "Sisteme Distribuite",
        "Securitatea Informațiilor",
      ],
    },
    {
      label: "Master Anul 2",
      icon: "🎓",
      options: [
        "Inteligența Artificială și Optimizare",
        "Ingineria Sistemelor Soft",
        "Lingvistica Computațională",
        "Studii Avansate în Informatică",
        "Sisteme Distribuite",
        "Securitatea Informațiilor",
      ],
    },
  ];

  const handleGroupSelect = (anLabel, grupa) => {
    const an = anLabel.replace("Anul ", "").trim();
    setSelectedGroup(`${anLabel} - ${grupa}`);

    // Navigăm la URL-ul cu an și grupă
    navigate(`/orar/studenti/${an}/${grupa}`);

    const url = `http://localhost:34101/orar/grupa/${an}/${grupa}`;
    fetch(url)
      .then((res) => {
        if (!res.ok) {
          throw new Error("Eroare la fetch orar. Status: " + res.status);
        }
        return res.json();
      })
      .then((data) => {
        setScheduleData(data);
      })
      .catch((err) => {
        console.error("Eroare la preluarea orarului:", err);
      });
  };

  return (
    <div className="orar-container">
      {selectedGroup ? (
        <div className="orar-afisat">
          <h3>Orar pentru {selectedGroup}</h3>
          <ScheduleTable
            schedule={scheduleData}
            title={`Orar pentru ${selectedGroup}`}
          />
          <button
            className="orar-button inapoi"
            onClick={() => {
              setSelectedGroup(null);
              setScheduleData([]);
              navigate("/orar/studenti"); // Navigăm înapoi la secțiunea de studenti
            }}
          >
            🔙 Înapoi
          </button>
        </div>
      ) : (
        <>
          <div className="orar-titlu">
            <h1>Orar</h1>
            {!section && <h2>Alege o categorie</h2>}
            {section === "studenti" && <h2>Alege anul și grupa</h2>}
          </div>

          {!section && (
            <div className="orar-buttons">
              <button className="orar-button" onClick={() => navigate("/orar/studenti")}>
                🎓 Orar Studenți
              </button>
            </div>
          )}

          {section === "studenti" && (
            <>
              <div className="orar-buttons">
                {buttons.map((button, index) => (
                  <div
                    className="dropdown-container"
                    key={index}
                    ref={(el) => (dropdownRefs.current[index] = el)}
                  >
                    <button className="orar-button" onClick={(e) => toggleDropdown(index, e)}>
                      <span className="icon">{button.icon}</span>
                      {button.label}
                    </button>

                    {activeDropdown === index && (
                      <div className="dropdown-menu">
                        {button.options.map((option, i) => (
                          <div key={i}>
                            <button
                              className="dropdown-item"
                              onClick={() => handleGroupSelect(button.label, option)}
                            >
                              {option}
                            </button>
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </>
          )}
        </>
      )}
    </div>
  );
};

export default OrarToti;
