import React, { useState, useRef, useEffect } from "react";
import ScheduleTable from "../../components/ScheduleTable/ScheduleTable.jsx";
import "./OrarToti.css";

const OrarToti = () => {
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [orarGrupa, setOrarGrupa] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const dropdownRefs = useRef([]);

  const toggleDropdown = (index, e) => {
    e.stopPropagation();
    setActiveDropdown((prev) => (prev === index ? null : index));
    setSelectedGroup(null);
    setOrarGrupa([]);
  };

  const fetchOrarGrupa = async (grupa) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`http://localhost:34101/orar/grupa/${encodeURIComponent(grupa)}`);
      if (!response.ok) {
        throw new Error("Eroare la fetch");
      }
      const data = await response.json();
      setOrarGrupa(data);
    } catch (err) {
      setError("Eroare la fetch");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleClickOutside = (event) => {
    if (dropdownRefs.current.every((ref) => ref && !ref.contains(event.target))) {
      setActiveDropdown(null);
    }
  };

  useEffect(() => {
    document.addEventListener("click", handleClickOutside);
    return () => {
      document.removeEventListener("click", handleClickOutside);
    };
  }, []);

  const buttons = [
    {
      label: "Orar studenți",
      icon: "🎓",
      options: ["Anul 1 - Grupa A1", "Anul 1 - Grupa A2", "Anul 2 - Grupa B1"],
    },
    {
      label: "Orar profesori",
      icon: "👨‍🏫",
      options: ["Profesor 1", "Profesor 2"],
    },
  ];

  return (
    <div className="orar-container">
      <div className="orar-titlu">
        <h1>Orar</h1>
        <h2>Alege o categorie pentru a vizualiza detalii</h2>
      </div>

      <div className="orar-content">
        {/* Meniul din stânga */}
        <div className="orar-buttons">
          {buttons.map((button, index) => (
            <div
              className="dropdown-container"
              key={index}
              ref={(el) => (dropdownRefs.current[index] = el)}
            >
              <button
                className="orar-button"
                onClick={(e) => toggleDropdown(index, e)}
              >
                <span className="icon">{button.icon}</span>
                {button.label}
              </button>

              {activeDropdown === index && (
                <div className="dropdown-menu">
                  {button.options.map((option, i) => (
                    <button
                      key={i}
                      className="dropdown-item"
                      onClick={() => {
                        setSelectedGroup(option);
                        fetchOrarGrupa(option);
                        setActiveDropdown(null);
                      }}
                    >
                      {option}
                    </button>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>

        {/* Afișăm tabelul dacă o grupă este selectată */}
        <div className="orar-display">
          {selectedGroup ? (
            <div className="orar-afisat">
              <h3>Orar pentru {selectedGroup}</h3>
              {loading && <p>Se încarcă...</p>}
              {error && <p>{error}</p>}
              {orarGrupa.length > 0 ? (
                <ScheduleTable schedule={orarGrupa} title={`Orar pentru ${selectedGroup}`} />
              ) : (
                !loading && <p>Nu există orar pentru această grupă.</p>
              )}
            </div>
          ) : (
            <p>Selectează o opțiune pentru a vedea orarul.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default OrarToti;
