import React, { useState } from "react";
import "./SecretariatToti.css"; // Asigură-te că ai un fișier CSS pentru stilizare

const SecretariatToti = () => {
  const [selectedOption, setSelectedOption] = useState(null);

  const handleOptionClick = (option) => {
    setSelectedOption(option);
  };

  return (
    <div className="orar-container">
      <div className="orar-titlu">
        <h1>Secretariat</h1>
        <h2>Alege o opțiune</h2>
      </div>
      <div className="orar-buttons">
        <button
          className="orar-button"
          onClick={() => handleOptionClick("decontari")}
        >
          <span className="icon">D</span>
          Cerere Decontări
        </button>
        <button
          className="orar-button"
          onClick={() => handleOptionClick("adeverinte")}
        >
          <span className="icon">A</span>
          Cerere Adeverințe
        </button>
      </div>
      {selectedOption === "decontari" && (
        <div className="orar-output">
          <h2>Cerere Decontări</h2>
          <p>Aici poți completa cererea pentru decontări.</p>
        </div>
      )}
      {selectedOption === "adeverinte" && (
        <div className="orar-output">
          <h2>Cerere Adeverințe</h2>
          <p>Aici poți completa cererea pentru adeverințe.</p>
        </div>
      )}
    </div>
  );
};

export default SecretariatToti;
