import React, { useState } from "react";

const SecretariatToti = () => {
  const [selectedOption, setSelectedOption] = useState(null);

  const handleOptionClick = (option) => {
    setSelectedOption(option);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Secretariat</h1>
      <div style={{ marginBottom: "20px" }}>
        <button
          onClick={() => handleOptionClick("decontari")}
          style={{ marginRight: "10px" }}
        >
          Cerere Decontări
        </button>
        <button onClick={() => handleOptionClick("adeverinte")}>
          Cerere Adeverințe
        </button>
      </div>
      {selectedOption === "decontari" && (
        <div>
          <h2>Cerere Decontări</h2>
          <p>Aici poți completa cererea pentru decontări.</p>
        </div>
      )}
      {selectedOption === "adeverinte" && (
        <div>
          <h2>Cerere Adeverințe</h2>
          <p>Aici poți completa cererea pentru adeverințe.</p>
        </div>
      )}
    </div>
  );
};

export default SecretariatToti;
