import React, { useState } from "react";
import "./SecretariatToti.css";

const SecretariatToti = () => {
  const [currentPage, setCurrentPage] = useState("main"); // Starea pentru pagina curentă

  const handleBackClick = () => {
    setCurrentPage("main"); // Revine la pagina principală
  };

  if (currentPage === "cerere-decontari") {
    return (
      <div className="secretariat-container">
        <h1>Cerere Decontări</h1>
        <form className="secretariat-form">
          <label>
            Nume:
            <input type="text" name="nume" placeholder="Introdu numele" />
          </label>
          <label>
            Prenume:
            <input type="text" name="prenume" placeholder="Introdu prenumele" />
          </label>
          <label>
            IBAN:
            <input type="text" name="iban" placeholder="Introdu IBAN-ul" />
          </label>
          <label>
            Suma solicitată:
            <input type="number" name="suma" placeholder="Introdu suma" />
          </label>
          <button type="submit" className="submit-button">Trimite Cererea</button>
        </form>
        <button onClick={handleBackClick} className="back-button">Înapoi</button>
      </div>
    );
  }

  return (
    <div className="secretariat-container">
      <div className="secretariat-titlu">
        <h1>Secretariat</h1>
        <h2>Alege o opțiune</h2>
      </div>
      <div className="secretariat-buttons">
        <button
          className="secretariat-button"
          onClick={() => setCurrentPage("cerere-decontari")}
        >
          <span className="icon">🧾</span>
          Cerere Decontări
        </button>
        <button
          className="secretariat-button"
          onClick={() => setCurrentPage("adeverinte")}
        >
          <span className="icon">📝</span>
          Cerere Adeverințe
        </button>
        <button
          className="secretariat-button"
          onClick={() => setCurrentPage("vezi-cereri")}
        >
          <span className="icon">📄</span>
          Istoric Cereri
        </button>
      </div>
      {currentPage === "adeverinte" && (
        <div className="secretariat-output">
          <h2>Cerere Adeverințe</h2>
        </div>
      )}
      {currentPage === "vezi-cereri" && (
        <div className="secretariat-output">
          <h2>Istoric Cereri</h2>
        </div>
      )}
    </div>
  );
};

export default SecretariatToti;
